import "core-js/modules/esnext.iterator.constructor.js";
import "core-js/modules/esnext.iterator.filter.js";
import axios from "axios";
import AddCoupon from './AddCoupon.vue';
import moment from "moment/moment";
export default {
  name: 'UserCouponView',
  components: {
    AddCoupon
  },
  data() {
    return {
      activeTab: 'all',
      //默认为all
      userId: null,
      shop_id: null,
      shopCoupons: [],
      showCoupons: [],
      //展示的优惠券
      selectIndex: 0,
      selectRow: [],
      products: [],
      //店铺全部商品
      showAddCouponDialog: false,
      loading: false,
      platformCouponDialogVisible: false,
      // 平台券弹窗是否显示
      shopCouponDialogVisible: false,
      // 店铺券弹窗是否显示
      platformScopeData: [],
      //存储返回来的平台券范围
      shopScopeData: [],
      confirmDeleteDialogVisible: false,
      editPendingCouponDialogVisible: false,
      editActiveCouponDialogVisible: false,
      updateScopeDialogVisible: false,
      updateScopeForm: {
        selectedProductIds: []
      },
      beforeScopeProductIds: [],
      // 存储商店的所有商品
      beforeScopeProductNames: [],
      couponDetails: [],
      editCouponForm: {
        coupon_id: null,
        start_time: '',
        end_time: '',
        scope: '',
        request: 0,
        off: 0,
        total: 0,
        claim_limit: 0,
        max_unused_count: 0
      },
      pauseCouponDialogVisible: false,
      resumeCouponDialogVisible: false
    };
  },
  computed: {},
  methods: {
    // 重新加载优惠券列表
    async refreshCoupons() {
      this.loading = true;
      try {
        const response = await axios.get(`http://localhost:8081/coupon/shop/${this.shop_id}`);
        if (response.data != null) {
          this.shopCoupons = response.data;
          if (this.activeTab === 'all') {
            this.showCoupons = this.shopCoupons; // 更新显示的优惠券列表
          } else {
            this.showCoupons = this.shopCoupons.filter(item => item.coupon_status === this.activeTab);
          }
        }
      } catch (error) {
        console.error("重新加载优惠券数据失败", error);
        this.$message.error('重新加载优惠券数据失败: ' + error.response.data);
      } finally {
        this.loading = false;
      }
    },
    addComplete() {
      this.showAddCouponDialog = false;
      this.refreshCoupons(); // 重新加载优惠券列表
    },
    confirmDeleteCoupon(row, index) {
      this.selectRow = row;
      this.selectIndex = index;
      this.confirmDeleteDialogVisible = true;
    },
    async deleteCouponConfirm() {
      const couponId = this.selectRow.coupon_id;
      try {
        const response = await axios.delete(`http://localhost:8081/coupon/delete/${couponId}`);
        if (response.status === 200) {
          this.$message.success('优惠券删除成功');
          await this.refreshCoupons(); // 重新加载优惠券列表
          this.showCoupons = this.activeTab === 'all' ? this.shopCoupons : this.shopCoupons.filter(item => item.coupon_status === this.activeTab);
        } else {
          this.$message.error('优惠券删除失败');
        }
      } catch (error) {
        this.$message.error(error.response.data);
      } finally {
        this.confirmDeleteDialogVisible = false;
      }
    },
    editCoupon(row, index) {
      this.selectRow = row;
      this.selectIndex = index;
      this.editCouponForm = {
        ...row
      }; // 深拷贝行数据到表单
      this.editPendingCouponDialogVisible = true;
    },
    editCoupon2(row, index) {
      this.selectRow = row;
      this.selectIndex = index;
      this.editCouponForm = {
        ...row
      }; // 深拷贝行数据到表单
      this.editActiveCouponDialogVisible = true;
    },
    submitEditPendingCoupon() {
      this.$refs.editPendingCouponForm.validate(async valid => {
        if (valid) {
          try {
            const couponData = {
              "coupon_id": this.editCouponForm.coupon_id,
              "start_time": moment(this.editCouponForm.start_time).format('YYYY-MM-DD HH:mm:ss'),
              "end_time": moment(this.editCouponForm.end_time).format('YYYY-MM-DD HH:mm:ss'),
              "request": this.editCouponForm.request,
              "off": this.editCouponForm.off,
              "total": this.editCouponForm.total,
              "claim_limit": this.editCouponForm.claim_limit,
              "max_unused_count": this.editCouponForm.max_unused_count
            };
            const response = await axios.post('http://localhost:8081/coupon/update/pending', couponData);
            if (response.status === 200) {
              this.$message.success('未生效优惠券修改成功');
              this.editPendingCouponDialogVisible = false;
              await this.refreshCoupons(); // 重新加载优惠券列表
            } else {
              this.$message.error(response.data);
            }
          } catch (error) {
            this.$message.error('未生效优惠券修改失败: ' + error.response.data);
          }
        }
      });
    },
    submitEditActiveCoupon() {
      this.$refs.editActiveCouponForm.validate(async valid => {
        if (valid) {
          try {
            const couponData = {
              "coupon_id": this.editCouponForm.coupon_id,
              "total": this.editCouponForm.total,
              "claim_limit": this.editCouponForm.claim_limit,
              "max_unused_count": this.editCouponForm.max_unused_count
            };
            const response = await axios.post('http://localhost:8081/coupon/update/active', couponData);
            if (response.status === 200) {
              this.$message.success('已生效优惠券修改成功');
              this.editActiveCouponDialogVisible = false;
              await this.refreshCoupons(); // 重新加载优惠券列表
            } else {
              this.$message.error(response.data);
            }
          } catch (error) {
            this.$message.error('已生效优惠券修改失败: ' + error.response.data);
          }
        }
      });
    },
    pauseCoupon(row, index) {
      this.selectRow = row;
      this.selectIndex = index;
      this.pauseCouponDialogVisible = true;
    },
    submitPauseCoupon() {
      const couponId = this.selectRow.coupon_id;
      axios.put(`http://localhost:8081/coupon/pause?coupon_id=${couponId}`).then(response => {
        if (response.status === 200) {
          this.$message.success('优惠券暂停发放成功');
          this.pauseCouponDialogVisible = false;
          this.refreshCoupons(); // 重新加载优惠券列表
        } else {
          this.$message.error(response.data);
        }
      }).catch(error => {
        this.$message.error('优惠券暂停发放失败: ' + error.response.data);
      });
    },
    resumeCoupon(row, index) {
      this.selectRow = row;
      this.selectIndex = index;
      this.resumeCouponDialogVisible = true;
    },
    submitResumeCoupon() {
      const couponId = this.selectRow.coupon_id;
      axios.put(`http://localhost:8081/coupon/resume?coupon_id=${couponId}`).then(response => {
        if (response.status === 200) {
          this.$message.success('优惠券恢复领取成功');
          this.resumeCouponDialogVisible = false;
          this.refreshCoupons(); // 重新加载优惠券列表
        } else {
          this.$message.error(response.data);
        }
      }).catch(error => {
        this.$message.error('优惠券恢复领取失败: ' + error.response.data);
      });
    },
    updateCouponScope(row, index) {
      this.selectRow = row;
      this.selectIndex = index;
      this.updateScopeDialogVisible = true;
      this.fetchScopeDetails();
    },
    fetchScopeDetails() {
      const couponId = this.selectRow.coupon_id;
      axios.post(`http://localhost:8081/coupon/selectById`, {
        id: couponId
      }).then(response => {
        if (response.status === 200) {
          this.couponDetails = response.data;
          console.log("获取的优惠券详情:", this.couponDetails);
          this.beforeScopeProductIds = this.couponDetails.scope || [];
          // this.beforeScopeProductNames = this.couponDetails.name || [];
        } else {
          this.$message.error('获取优惠券详情失败');
        }
      }).catch(error => {
        this.$message.error('获取的优惠券详情: ' + error.response.data);
      });
    },
    submitUpdateScope() {
      const couponId = this.selectRow.coupon_id;
      const productIds = this.updateScopeForm.selectedProductIds;
      console.log("更新范围提交，新的范围数组：", productIds);
      axios.post('http://localhost:8081/coupon/updateShopCouponScope', {
        coupon_id: couponId,
        productIds: productIds
      }).then(response => {
        if (response.status === 200) {
          this.$message.success('优惠券适用范围更新成功');
          this.updateScopeDialogVisible = false;
          this.refreshCoupons(); // 重新加载优惠券列表
        } else {
          this.$message.error('更新优惠券适用范围失败: ' + response.data);
        }
      }).catch(error => {
        this.$message.error(error.response.data);
      });
    },
    // 获取优惠券数据
    async getAllShopCoupons() {
      this.loading = true;
      this.user_id = this.$store.getters.userId;
      console.log("当前用户id为：" + this.user_id);
      try {
        const shopResponse = await axios.post('http://localhost:8081/shop/getByUser_id', {
          id: this.user_id
        });
        if (shopResponse.data != null) {
          this.shop_id = shopResponse.data.shop_id;
          console.log("当前店铺id为：" + this.shop_id);
          try {
            const response = await axios.post('http://localhost:8081/product/getAllByShop_id', {
              id: this.shop_id
            });
            if (response.data) {
              this.products = response.data;
              console.log("商品列表:", this.products);
            } else {
              this.$message.warning('该店铺下没有商品');
            }
          } catch (error) {
            this.$message.error('获取商品信息失败: ' + error.response.data);
          }
          try {
            const shopCouponsResponse = await axios.get(`http://localhost:8081/coupon/shop/${this.shop_id}`);
            if (shopCouponsResponse.data != null) {
              this.shopCoupons = shopCouponsResponse.data; //全部的店铺券
              console.log("shopCoupons:", this.shopCoupons);
              // 不需要扁平化
              this.showCoupons = this.shopCoupons;
            } else {
              this.$message.warning('该店铺下没有优惠券');
            }
          } catch (error) {
            console.error("获取店铺优惠券数据失败", error);
            this.$message.error('获取店铺优惠券数据失败: ' + error.response.data);
          }
        } else {
          this.$message.warning('未找到店铺信息');
        }
      } catch (error) {
        console.error("获取店铺信息失败", error);
        this.$message.error('获取店铺信息失败: ' + error.response.data);
      } finally {
        this.loading = false;
      }
    },
    handleTabClick(tab) {
      const status = tab.name;
      if (status === 'all') {
        this.showCoupons = this.shopCoupons;
      } else {
        this.showCoupons = this.shopCoupons.filter(item => item.coupon_status === status);
      }
    },
    numberFormatter(row, column, value) {
      return `${value.toFixed(2)}`;
    },
    // 金额格式化函数，将数字格式化为人民币形式
    currencyFormatter(row, column, value) {
      // 保留两位小数并添加人民币符号
      return `￥${value.toFixed(2)}`;
    },
    // 券的状态
    statusFormatter(row, column, value) {
      if (value === 'Pending') {
        return "待生效";
      } else if (value === 'Active') {
        return "可领取";
      } else if (value === 'Paused') {
        return "暂停领取";
      } else if (value === 'Expired') {
        return "已失效";
      } else {
        return "未知状态";
      }
    },
    // 查看券的可用范围
    async viewCouponRange(row, index) {
      this.selectIndex = index + 1;
      const couponId = row.coupon_id; // 获取当前行的 coupon_id
      try {
        const response = await axios.get(`http://localhost:8081/userCoupon/scope/details?coupon_id=${couponId}`);
        const scopeDetails = response.data;
        // 这里只有平台券
        // this.platformScopeData = scopeDetails.platform || [];
        // console.log("platform:",this.platformScopeData);
        // this.platformCouponDialogVisible = true; // 显示平台券弹窗

        // 这里只有店铺券
        this.shopScopeData = scopeDetails.shop || [];
        console.log("shop:", this.shopScopeData);
        this.shopCouponDialogVisible = true; // 显示店铺券弹窗
      } catch (error) {
        console.error("获取范围数据失败:", error);
      }
    },
    // 清空平台券数据
    clearPlatformScopeData() {
      this.platformScopeData = [];
    },
    // 清空店铺券数据
    clearShopScopeData() {
      this.shopScopeData = [];
    },
    parseJson(str) {
      return JSON.parse(str);
    }
  },
  created() {
    console.log('平台优惠券数据加载开始');
    this.getAllShopCoupons();
    console.log('平台优惠券数据加载完成');
    // 模拟从后端获取数据
    this.loading = false;
  }
};
