import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    user: JSON.parse(localStorage.getItem('user')) || null,
    isAuthenticated: !!localStorage.getItem('user')
  },
  getters: {
    isAuthenticated: state => state.isAuthenticated,
    currentUser: state => state.user,
    userId: state => state.user ? state.user.user_id : null,
    username: state => state.user ? state.user.username : null
  },
  mutations: {
    setUser(state, user) {
      state.user = user;
      state.isAuthenticated = !!user;
      // 保存到 localStorage
      if (user) {
        localStorage.setItem('user', JSON.stringify(user));
      } else {
        localStorage.removeItem('user');
      }
    }
  },
  actions: {
    async login({ commit }, credentials) {
      try {
        // 获取用户详细信息
        const response = await fetch(`http://localhost:8081/users/detail/${credentials.username}`);
        const data = await response.json();

        if (data.status === 'success') {
          commit('setUser', data.data);
          return true;
        }
        return false;
      } catch (error) {
        console.error('Error fetching user details:', error);
        return false;
      }
    },
    logout({ commit }) {
      commit('setUser', null);
    },
    updateUserProfile({ commit, state }, updatedProfile) {
      const updatedUser = { ...state.user, ...updatedProfile };
      commit('setUser', updatedUser);
    }
  }
})
